const underheadMouse=document.querySelector(".underheader-nav__mouse");function scrollTop(e){let n=this.getBoundingClientRect().y+this.getBoundingClientRect().height;e||(n=document.documentElement.clientHeight-n);let t=setInterval(()=>{e?window.scrollBy(0,10):window.scrollBy(0,-10),(n-=10)<0&&clearInterval(t)},0)}underheadMouse.addEventListener("click",scrollTop.bind(underheadMouse,!0),!0);