import Swiper from"https://cdn.jsdelivr.net/npm/swiper@8/swiper-bundle.esm.browser.min.js";const confidenceSlider=new Swiper(".confirence__swiper",{slidesPerView:1,spaceBetween:45,navigation:{nextEl:".confirence__next",prevEl:".confirence__prev"},breakpoints:{768:{slidesPerView:3}}}),samplesSlider=new Swiper(".samples__slider",{slidesPerView:2,spaceBetween:16,slidesPerGroup:2,navigation:{nextEl:".samples__next",prevEl:".samples__prev"},breakpoints:{768:{slidesPerView:4,spaceBetween:30}}}),menu=document.querySelector(".switch__menu"),arrangeSlider=new Swiper(".arrange__swiper",{slidesPerView:1,navigation:!1,spaceBetween:100,pagination:{el:".swiper-pagination",type:"progressbar"}});function swiperMenuChanger(e){const i=event.target.dataset.index||e.slider.activeIndex;if(!i&&0!==i)return;const n=e.menu.querySelectorAll(e.menuItemSelector);n.forEach(i=>i.classList.remove(e.menuItemSelectorActive));for(let r=0;r<=i;r++)n[r].classList.add(e.menuItemSelectorActive);e.slider.slideTo(i)}menu.addEventListener("click",swiperMenuChanger.bind(menu,{slider:arrangeSlider,menu:menu,menuItemSelector:".switch__item",menuItemSelectorActive:"switch__item_active"})),arrangeSlider.on("slideChange",swiperMenuChanger.bind(menu,{slider:arrangeSlider,menu:menu,menuItemSelector:".switch__item",menuItemSelectorActive:"switch__item_active"}));